public class phoneFactory implements abstractFactory<phone>{                                                                                                                                @Override                                                                              public phone produce(String type ){                                                                                                                                                      if("Samsung".equalsIgnoreCase(type))                                                            return new samsung();                                                         else if("Iphone".equalsIgnoreCase(type))                                                         return new iphone();                                                         else                                                                                           return null;                                                                                                                                                  }

		@Override                                                                              public phone  getFactoryName(String name){                                                        return name;                                                                                                                                                          }  
}    
